        idSaldoCollection = v;
      },
      get statePopupPanel() {
        return statePopupPanel;
      },
      set statePopupPanel(v) {
        statePopupPanel = v;
      },
      get textPopupPanel() {
        return textPopupPanel;
      },
      set textPopupPanel(v) {
        textPopupPanel = v;
