      alertPopupPanel,
      get stateSuccessPopup() {
        return stateSuccessPopup;
      },
      set stateSuccessPopup(v) {
        stateSuccessPopup = v;
      },
      get textSuccess() {
        return textSuccess;
      },
      set textSuccess(v) {
        textSuccess = v;
      },
      successPopup,
      ListTransactions,
      ref,
      get useStore() {
        return useStore;
      },
      get db() {
        return db;
      },
      get collection() {
        return collection;
      },
      get addDoc() {
        return addDoc;
      },
      get onSnapshot() {
        return onSnapshot;
      },
