  'use strict';
  this.ensureKeyMapInitialized_();
  // We need to get the values to know how many keys to add.
  const vals = Array.from(this.keyMap_.values());
  const keys = Array.from(this.keyMap_.keys());
  const rv = [];
  for (let i = 0; i < keys.length; i++) {
    const val = vals[i];
