/** @override */
goog.net.FetchXmlHttp.prototype.send = function(opt_data) {
  'use strict';
  if (this.readyState != goog.net.FetchXmlHttp.RequestState.OPENED) {
    this.abort();
    throw new Error('need to call open() first. ');
  }

