        /** @type {!Uint8Array} */ (result.value) :
        new Uint8Array(0);
    const newText =
        this.textDecoder_.decode(dataPacket, {stream: !result.done});
    if (newText) {
      this.responseText += newText;
      this.response = this.responseText;
